@import '~@alitajs/theme/es/index.less';
@import '~antd-mobile/lib/swipe-action/style/index';
@import './assets/svg';

@prefixCls:alita-upload-file;

.@{prefixCls} {
    overflow: hidden;
    border-radius: 6 * @hd;
    background-color: @fill-base;
    &-header {
        display: flex;
        flex-direction: row;
        align-items: center;
        justify-content: space-between;
        padding: 16 * @hd 16 * @hd 10 * @hd;
        font-size: 17 * @hd;
        font-weight: 500;
    }
    &-extra {
        position: relative;
        width: 18 * @hd;
        height: 18 * @hd;
        background-repeat: no-repeat;
        background-position: center;
        background-size: cover;
        .svg-upload-icon();
        input {
            width: 100%;
            height: 100%;
            opacity: 0;
        }
    } 


    &-fileitem {
        display: flex;
        flex-direction: row;
        align-items: center;
        box-sizing: border-box;
        min-height: 73 * @hd;
        padding: 16 * @hd;
        border-bottom: @border-width-sm solid @border-color-base;
    }
    &-icon {
        width: 40 * @hd;
        height: 40 * @hd;
        margin-right: 12 * @hd;
        background-repeat: no-repeat;
        background-size: cover;
        background-position: center;
        &-ppt {
            .svg-ppt-icon();
        }
        &-docx {
            .svg-docx-icon();
        }
        &-xlsx {
            .svg-xlsx-icon();
        }
        &-other {
            .svg-other-icon();
        }
        &-img {
            .svg-img-icon();
        }
        &-pdf {
            .svg-pdf-icon();
        }
    }

    &-text {
        flex: 1;
        word-break: break-all;
        color: @color-text-base;
        font-size: @font-size-base;
    }
