      <WhiteSpace />
      <Button onClick={showShareActionSheet}>showShareActionSheet</Button>
      <WhiteSpace />
      <Button onClick={showShareActionSheetMulpitleLine}>
        showShareActionSheetMulpitleLine
      </Button>
    </WingBlank>
  );
};

export default ActionSheetExmple;

