import React from 'react';
import { InputItem, List, Toast } from '@alitajs/antd-mobile';

class ErrorInputExample extends React.Component {
  state = {
    hasError: false,
    value: '',
  };
  onErrorClick = () => {
    if (this.state.hasError) {
      Toast.info('Please enter 11 digits');
    }
  };
  onChange = (value) => {
    if (value.replace(/\s/g, '').length < 11) {
      this.setState({
        hasError: true,
      });
    } else {
      this.setState({
        hasError: false,
      });
    }
    this.setState({
      value,
    });
  };
  render() {
    return (
      <div>
        <List renderHeader={() => 'Confirm when typing'}>
          <InputItem
            type="phone"
            placeholder="input your phone"
            error={this.state.hasError}
            onErrorClick={this.onErrorClick}
            onChange={this.onChange}
            value={this.state.value}
          >
            手机号码
          </InputItem>
        </List>
      </div>
    );
  }
}

export default ErrorInputExample;

import React from 'react';
import { createForm } from 'rc-form';
import { InputItem, List } from '@alitajs/antd-mobile';

// 通过自定义 moneyKeyboardWrapProps 修复虚拟键盘滚动穿透问题
// https://github.com/ant-design/ant-design-mobile/issues/307
// https://github.com/ant-design/ant-design-mobile/issues/163
const isIPhone = new RegExp('\\biPhone\\b|\\biPod\\b', 'i').test(
  window.navigator.userAgent,
);
let moneyKeyboardWrapProps;
if (isIPhone) {
  moneyKeyboardWrapProps = {
    onTouchStart: (e) => e.preventDefault(),
  };
}

class H5NumberInputExample extends React.Component {
  state = {
    type: 'money',
  };
  render() {
    const { getFieldProps } = this.props.form;
    const { type } = this.state;
    return (
      <div>
        <List>
          <InputItem
            {...getFieldProps('money3')}
            type={type}
            defaultValue={100}
            placeholder="start from left"
            clear
            moneyKeyboardAlign="left"
            moneyKeyboardWrapProps={moneyKeyboardWrapProps}
          >
            光标在左
          </InputItem>
          <InputItem
            type={type}
            placeholder="start from right"
            clear
            onChange={(v) => {
              console.log('onChange', v);
            }}
            onBlur={(v) => {
              console.log('onBlur', v);
            }}
            moneyKeyboardWrapProps={moneyKeyboardWrapProps}
          >
            光标在右
          </InputItem>
          <InputItem
            {...getFieldProps('money2', {
              normalize: (v, prev) => {
                if (v && !/^(([1-9]\d*)|0)(\.\d{0,2}?)?$/.test(v)) {
                  if (v === '.') {
                    return '0.';
                  }
                  return prev;
                }
                return v;
              },
            })}
            type={type}
            placeholder="money format"
            ref={(el) => (this.inputRef = el)}
            onVirtualKeyboardConfirm={(v) =>
              console.log('onVirtualKeyboardConfirm:', v)
            }
            clear
            moneyKeyboardWrapProps={moneyKeyboardWrapProps}
          >
            数字键盘
          </InputItem>
          <List.Item>
            <div
              style={{ width: '100%', color: '#108ee9', textAlign: 'center' }}
              onClick={() => this.inputRef.focus()}
            >
              click to focus
            </div>
          </List.Item>
        </List>
      </div>
    );
  }
}

const H5NumberInputExampleWrapper = createForm()(H5NumberInputExample);
export default H5NumberInputExampleWrapper;

